# https://www.acmicpc.net/problem/13705
from decimal import *
getcontext().prec = 150
D = Decimal
decpi = Decimal('3.')
getcontext().rounding=ROUND_HALF_UP
def decsin(x):
	x = x%(2*decpi)
	i, lasts, s, fact, num, sign = D(1), D(0), D(x), D(1), D(x), D(1)
	while s != lasts:
		lasts = s
		i += 2
		fact *= i * (i-1)
		num *= x * x
		sign *= -1
		s += num / fact * sign
	return D(s)

a,b,c = map(Decimal, input().split())
x = Decimal()
lo = Decimal(0)
hi = Decimal(500000)
tol = Decimal(1.0e-60)
while (Decimal(hi-lo).copy_abs()) >= tol :
	x = (lo+hi)/2
	if Decimal(a*x + b * decsin(x))<Decimal(c):
		lo = x
	else:
		hi = x
print(round(x, 6))